mapper.select:
SELECT 

FROM hotword a

WHERE a.id = #{id}

SELECT 

FROM hotword a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.hotword LIKE 
			'%'||#{hotword}||'%'
			'%'+#{hotword}+'%'
			concat('%',#{hotword},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM hotword a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE hotword SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
